INSERT INTO `new_zbgg_index` VALUES ('e90bf9fc-b08e-4d7c-9503-a444f6096134', '北方机电工业学校购买半军事化管理服务项目公开招标公告', '2023-05-02', '学校', '北方机电工业学校（河北省机电工程技师学院）', '张家口荣盛招标代理有限公司', NULL, 'zbgg', '招标公告', '2023-05-02', '<h1 class=\"ql-align-center\">北方机电工业学校购买半军事化管理服务项目公开招标公告</h1><p>项目编号：I1301000075049880001</p><p>招标方式：公开招标</p><p>项目地点：张家口市-桥东区</p><p>所属行业：商务服务业</p><p>	项目概况 北方机电工业学校购买半军事化管理服务项目招标项目的潜在投标人应在 凡有意参加投标者，请及时登录惠招标电子招投标交易平台下载公开招标文件获取招标文件，并于 2023年04月06日09点30分 （北京时间）前递交投标文件。</p><p><strong style=\"color: rgb(4, 89, 141);\">北方机电工业学校购买半军事化管理服务项目公开招标公告</strong></p><p>发布时间：&nbsp;<span style=\"color: rgb(86, 86, 86);\">2023-03-14</span><strong style=\"color: rgb(102, 102, 102);\">一、项目基本情况</strong></p><p><span style=\"color: rgb(74, 74, 74);\">项目编号：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;RS-2023-ZC003</span></p><p><span style=\"color: rgb(74, 74, 74);\">项目名称：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;北方机电工业学校购买半军事化管理服务项目</span></p><p><span style=\"color: rgb(74, 74, 74);\">预算金额：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;1404000.00</span></p><p><span style=\"color: rgb(74, 74, 74);\">最高限价：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;1404000.00</span></p><p><span style=\"color: rgb(74, 74, 74);\">采购需求：</span><span style=\"color: rgb(86, 86, 86);\">半军事化管理服务</span></p><p><span style=\"color: rgb(74, 74, 74);\">合同履行期限：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;自签订合同之日起一年</span></p><p><span style=\"color: rgb(74, 74, 74);\">本项目（是/否）接受联合体投标：</span><span style=\"color: rgb(86, 86, 86);\">否</span></p><p><strong style=\"color: rgb(102, 102, 102);\">二、申请人的资格要求</strong></p><p><span style=\"color: rgb(86, 86, 86);\">1.满足《中华人民共和国政府采购法》第二十二条规定；</span></p><p><span style=\"color: rgb(86, 86, 86);\">2.落实政府采购政策需满足的资格要求： 《财政部工业和信息化部关于印发&lt;政府采购促进中小企业发展管理办法》（财库〔2020〕46号），本项目专门面向小微企业采购，小微企业预留份额100%（监狱企业、残疾人福利性单位视为小微企业）；</span></p><p><span style=\"color: rgb(86, 86, 86);\">3.本项目的特定资格要求： 无</span></p><p><strong style=\"color: rgb(102, 102, 102);\">三、获取招标文件</strong></p><p><span style=\"color: rgb(74, 74, 74);\">时间：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;2023年03月15日至 2023年03月21日， 08:30-12:00-12:00-17:30</span></p><p><span style=\"color: rgb(86, 86, 86);\">（北京时间，法定节假日除外）</span><span style=\"color: rgb(74, 74, 74);\">&nbsp;地点：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;凡有意参加投标者，请及时登录惠招标电子招投标交易平台下载公开招标文件</span></p><p><span style=\"color: rgb(74, 74, 74);\">方式：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;其它</span></p><p><span style=\"color: rgb(74, 74, 74);\">售价：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;0</span></p><p><strong style=\"color: rgb(102, 102, 102);\">四、提交投标文件截止时间、开标时间和地点</strong></p><p><strong style=\"color: rgb(86, 86, 86);\">2023年04月06日09点30分（北京时间）</strong></p><p><strong style=\"color: rgb(102, 102, 102);\">地点：&nbsp;</strong><strong style=\"color: rgb(86, 86, 86);\">张家口市公共资源交易中心第一开标室，供应商应在截止时间前通过登录惠招标电子招投标交易平台,在线提交电子投标文件，并CA加密投标</strong></p><p><strong style=\"color: rgb(102, 102, 102);\">五、公告期限</strong></p><p><strong style=\"color: rgb(86, 86, 86);\">自本公告发布之日起5个工作日。</strong></p><p><strong style=\"color: rgb(102, 102, 102);\">六、其他补充事宜</strong></p><p><span style=\"color: rgb(86, 86, 86);\">1、本公告发布媒体：中国政府采购网、河北省政府采购网、河北省公共资源交易服务平台、惠招标电子招投标交易平台；2、本项目采用全流程电子招投标，无需供应商到达开标现场，在开标时间内供应商在电子平台自行解密投标文件，若在开标时间内未成功解密投标文件的将为无效投标。</span></p><p><strong style=\"color: rgb(102, 102, 102);\">七、对本次招标提出询问，请按以下方式联系。</strong></p><p><span style=\"color: rgb(74, 74, 74);\">1.采购人信息</span></p><p><span style=\"color: rgb(74, 74, 74);\">名 称：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;北方机电工业学校（河北省机电工程技师学院）</span></p><p><span style=\"color: rgb(74, 74, 74);\">地址：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;张家口市桥东区钻石中路18号</span></p><p><span style=\"color: rgb(74, 74, 74);\">联系方式：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;黄老师 0313-4039219</span></p><p><span style=\"color: rgb(74, 74, 74);\">2.采购代理机构信息</span></p><p><span style=\"color: rgb(74, 74, 74);\">名 称：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;张家口荣盛招标代理有限公司</span></p><p><span style=\"color: rgb(74, 74, 74);\">地　址：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;河北省张家口市桥西区工业西街8号1号楼3层01</span></p><p><span style=\"color: rgb(74, 74, 74);\">联系方式：&nbsp;</span><span style=\"color: rgb(86, 86, 86);\">贾璐 0313-8076909</span></p><p><span style=\"color: rgb(74, 74, 74);\">3.项目联系方式</span></p><p><span style=\"color: rgb(74, 74, 74);\">项目联系人：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;贾璐</span></p><p><span style=\"color: rgb(74, 74, 74);\">电　话：</span><span style=\"color: rgb(86, 86, 86);\">&nbsp;0313-8076909</span></p>', '2023-05-02 23:08:57');
